mapper.select:
select a.* ,@rank:=@rank+1 as sort from ( SELECT CMCC_prov_prvd_id as provinceCode, short_name as provName, Aud_trm as auditMonth, no_real_name_num as noRealNameNum, tol_subs_num as tolSubsNum, ifnull(no_real_name_per,0) as noRealNamePer, real_name_per as realNamePer FROM hpeapb.sum_smzdjl_prvd where 1=1
and Aud_trm =#{currSumEndDate }
ORDER BY real_name_per desc ) a ,(SELECT @rank:=0) b

SELECT Gen_date, Aud_trm, CMCC_prov_prvd_id, short_name, CMCC_prov_id, CMCC_prvd_nm_short, no_real_name_num, tol_subs_num, no_real_name_per, real_name_per FROM hpeapb.sum_smzdjl_cty where 1=1
and cmcc_prov_prvd_id =#{provinceCode}
and Aud_trm =#{currSumEndDate }
order by real_name_per LIMIT 3

SELECT Gen_date, Aud_trm, CMCC_prov_prvd_id, short_name, CMCC_prov_id, CMCC_prvd_nm_short, no_real_name_num, tol_subs_num, no_real_name_per, real_name_per FROM hpeapb.sum_smzdjl_cty where 1=1
and cmcc_prov_prvd_id =#{params.provinceCode}
and Aud_trm =#{params.currSumEndDate }
order by Aud_trm desc,no_real_name_num desc

SELECT Gen_date, Aud_trm, CMCC_prov_prvd_id, short_name, CMCC_prov_id, CMCC_prvd_nm_short, no_real_name_num, tol_subs_num, no_real_name_per, real_name_per FROM hpeapb.sum_smzdjl_cty where 1=1
and cmcc_prov_prvd_id =#{provinceCode}
and Aud_trm =#{currSumEndDate }
order by Aud_trm desc,no_real_name_num desc

SELECT CMCC_prov_prvd_id, short_name, Aud_trm, no_real_name_num, tol_subs_num, no_real_name_per, real_name_per FROM hpeapb.sum_smzdjl_prvd where 1=1
and cmcc_prov_prvd_id =#{provinceCode}
and Aud_trm >=#{currSumBeginDate }
and Aud_trm <=#{currSumEndDate }
order by Aud_trm

SELECT CMCC_prov_prvd_id, short_name, aud_trm, no_real_name_num, tol_subs_num, no_real_name_per, (real_name_per)*100 as real_name_per FROM hpeapb.sum_smzdjl_prvd where 1=1
and cmcc_prov_prvd_id =#{provinceCode}
and Aud_trm >=#{currSumBeginDate }
and Aud_trm <=#{currSumEndDate }
ORDER BY real_name_per asc limit 1

SELECT DISTINCT
		Gen_date,
		Aud_trm,
		cmcc_prov_prvd_id,
		short_name,
		cmcc_prov_id,
		subs_id,
		cmcc_prvd_nm_short,
		(CASE WHEN MSISDN= '' THEN '' ELSE CONCAT(SUBSTR(MSISDN,1,(LENGTH(trim(MSISDN))-4)),'****') END)AS MSISDN,
		ent_dt,
		subs_stat_typ_cd,
		ent_chnl_id,
		subs_pay_typ_cd,
		subs_typ_cd,
		cust_id
FROM hpeapb.det_smzdjl
where 1=1


	and cmcc_prov_prvd_id =#{params.provinceCode}



		and subs_typ_cd =#{params.currUserType}



		and cmcc_prov_id =#{params.currCityType}



		and subs_stat_typ_cd =#{params.currUserStatus}



		and subs_pay_typ_cd =#{params.currPayType}




		and Aud_trm =#{params.currDetEndDate}



		and  ent_dt between #{params.currEntDtBegin} and #{params.currEntDtEnd} 


		and ent_dt >=#{params.currEntDtBegin}


		and ent_dt <=#{params.currEntDtEnd}

	order by Aud_trm desc,cmcc_prov_id

SELECT DISTINCT
		Gen_date,
		Aud_trm,
		cmcc_prov_prvd_id,
		short_name,
		cmcc_prov_id,
		subs_id,
		cmcc_prvd_nm_short,
		(CASE WHEN MSISDN= '' THEN '' ELSE CONCAT(SUBSTR(MSISDN,1,(LENGTH(trim(MSISDN))-4)),'****') END)AS MSISDN,
		ent_dt,
		subs_stat_typ_cd,
		ent_chnl_id,
		subs_pay_typ_cd,
		subs_typ_cd,
		cust_id
FROM hpeapb.det_smzdjl
where 1=1


	and cmcc_prov_prvd_id =#{provinceCode}



		and subs_typ_cd =#{currUserType}



		and cmcc_prov_id =#{currCityType}



		and subs_stat_typ_cd =#{currUserStatus}



		and subs_pay_typ_cd =#{currPayType}




		and Aud_trm =#{currDetEndDate}



		and  ent_dt between #{currEntDtBegin} and #{currEntDtEnd} 


		and ent_dt >=#{currEntDtBegin}


		and ent_dt <=#{currEntDtEnd}

	order by Aud_trm desc,cmcc_prov_id  LIMIT #{pageStar},#{pageEnd}
