mapper.select:
	select 
		aud_trm,IFNULL(sum(acct_cnt),0) as acct_cnt
	from 
		hpeapb.sox_sum_cqqfwtj_1302_prvd  
	where 1=1

         and aud_trm >= #{hz_startMonth} 



         and aud_trm <= #{hz_endMonth} 



		 and cmcc_prov_prvd_id = #{provId} 

	GROUP BY aud_trm 
	ORDER BY aud_trm asc

	select 
		IFNULL(AVG(acct_cnt),0) as avg_acc_cnt
	from 
		hpeapb.sox_sum_cqqfwtj_1302_prvd  
	where 1=1

         and aud_trm >= #{hz_startMonth} 



         and aud_trm <= #{hz_endMonth} 



		 and cmcc_prov_prvd_id = #{provId}

	select 
		aud_trm,acct_cnt as max_acc_cnt 
	from 
		hpeapb.sox_sum_cqqfwtj_1302_prvd  
	where 1=1 

         and aud_trm >= #{hz_startMonth} 



         and aud_trm <= #{hz_endMonth} 



		 and cmcc_prov_prvd_id = #{provId} 

	ORDER BY acct_cnt desc limit 1

select gen_date, aud_trm, cmcc_prov_prvd_id, short_name, cmcc_prov_id, cmcc_prvd_nm_short, acct_id, cust_id, org_nm, cust_stat_typ_nm , org_cust_lvl, org_busn_nm, acct_prd_ytm, dbt_amt from hpeapb.sox_det_cqqfwtj_1302_debt where 1=1
and aud_trm >= #{params.mx_startMonth}
and aud_trm <= #{params.mx_endMonth}
and cmcc_prov_prvd_id = #{params.provId}
ORDER BY aud_trm desc,acct_id ,acct_prd_ytm

select gen_date, aud_trm, cmcc_prov_prvd_id, short_name, cmcc_prov_id, cmcc_prvd_nm_short, acct_id, cust_id, org_nm, cust_stat_typ_nm , org_cust_lvl, org_busn_nm, acct_prd_ytm, dbt_amt from hpeapb.sox_det_cqqfwtj_1302_debt where 1=1
and aud_trm >= #{mx_startMonth}
and aud_trm <= #{mx_endMonth}
and cmcc_prov_prvd_id = #{provId}
ORDER BY aud_trm desc,acct_id ,acct_prd_ytm LIMIT #{pageStar},#{pageEnd}
