mapper.select:
SELECT gen_date, aud_trm, cmcc_prov_prvd_id, short_name, yjk_cnt, amt_sum, one_yjk_amt, zs_yjk_cnt, zs_amt_sum, cnt_tol, per_cnt, amt_tol, per_amt FROM sum_yjk_2001_prvd_ss
where 1=1
and aud_trm >= #{hz_startMonth}
and aud_trm <= #{hz_endMonth}
and cmcc_prov_prvd_id = #{provId}
order by Aud_trm

	SELECT
	ifnull(avg(yjk_cnt),0)as avg_yjk_cnt
	FROM
	sum_yjk_2001_prvd_ss
	where 1=1 


         and aud_trm >= #{hz_startMonth} 



         and aud_trm <= #{hz_endMonth} 



		and cmcc_prov_prvd_id = #{provId}

	SELECT
	aud_trm,
	b.max_yjk_cnt
	FROM
	hpeapb.sum_yjk_2001_prvd_ss t
	join (
		SELECT
	ifnull(max(yjk_cnt),0) as max_yjk_cnt
	FROM
	hpeapb.sum_yjk_2001_prvd_ss 
	where 1=1 
	and aud_trm >= #{hz_startMonth}
    and aud_trm <= #{hz_endMonth}
	and  cmcc_prov_prvd_id = #{provId}
	) b
	on b.max_yjk_cnt = t.yjk_cnt
	where 1=1 
	and aud_trm >= #{hz_startMonth}
    and aud_trm <= #{hz_endMonth}
	and cmcc_prov_prvd_id = #{provId}

SELECT GEN_DATE, AUD_TRM, CMCC_PROV_PRVD_ID, SHORT_NAME, CMCC_PROV_ID, CMCC_PRVD_NM_SHORT, IFNULL(sum(YJK_CNT),0) as SUM_YJK_CNT, AMT_SUM, ONE_YJK_AMT, ZS_YJK_CNT, ZS_AMT_SUM, CNT_TOL, PER_CNT, AMT_TOL, PER_AMT FROM sum_yjk_2001_cty_ss where 1=1
and aud_trm >= #{hz_startMonth}
and aud_trm <= #{hz_endMonth}
and cmcc_prov_prvd_id = #{provId}
GROUP BY CMCC_PROV_ID order by SUM_YJK_CNT desc limit 10

SELECT GEN_DATE, AUD_TRM, CMCC_PROV_PRVD_ID, SHORT_NAME, CMCC_PROV_ID, CMCC_PRVD_NM_SHORT, IFNULL(sum(YJK_CNT),0) as SUM_YJK_CNT, AMT_SUM, ONE_YJK_AMT, ZS_YJK_CNT, ZS_AMT_SUM, CNT_TOL, ifnull(sum(YJK_CNT)/sum(CNT_TOL),0) as AVG_PER_CNT, AMT_TOL, PER_AMT FROM sum_yjk_2001_cty_ss where 1=1
and aud_trm >= #{hz_startMonth}
and aud_trm <= #{hz_endMonth}
and cmcc_prov_prvd_id = #{provId}
GROUP BY CMCC_PROV_ID order by AVG_PER_CNT desc limit 10

	SELECT 

	ifnull(sum(yjk_cnt)/sum(cnt_tol),0) as AVG_PER_CNT

	FROM sum_yjk_2001_prvd_ss t
	where 1=1 


         and aud_trm >= #{hz_startMonth} 



         and aud_trm <= #{hz_endMonth} 



		and cmcc_prov_prvd_id = #{provId}

SELECT GEN_DATE, AUD_TRM, CMCC_PROV_PRVD_ID, SHORT_NAME, CMCC_PROV_ID, CMCC_PRVD_NM_SHORT, OPR_ID, concat(left(NM,1),'**') NM, ifnull(sum(YJK_CNT),0) as SUM_YJK_CNT, AMT_SUM, ONE_YJK_AMT, ifnull(sum(ZS_YJK_CNT),0) as SUM_ZS_YJK_CNT, ZS_AMT_SUM, CNT_TOL, PER_CNT, AMT_TOL, PER_AMT, ifnull(sum(DZKH_ZS_NUM),0) as SUM_DZKH_ZS_NUM, ifnull(sum(PLJH_ZS_NUM),0) as SUM_PLJH_ZS_NUM, ifnull(sum(JZZS_CZ_NUM),0) as SUM_JZZS_CZ_NUM FROM sum_yjk_2001_czy_ss
where 1=1
and aud_trm >= #{hz_startMonth}
and aud_trm <= #{hz_endMonth}
and cmcc_prov_prvd_id = #{provId}
and cmcc_prov_id = #{cityId}
group by OPR_ID order by SUM_YJK_CNT desc limit 10

SELECT GEN_DATE, AUD_TRM, CMCC_PROV_PRVD_ID, SHORT_NAME, CMCC_PROV_ID, CMCC_PRVD_NM_SHORT, OPR_ID, concat(left(NM,1),'**') NM, ifnull(sum(YJK_CNT),0) as SUM_YJK_CNT, ifnull(sum(AMT_SUM),0) as SUM_AMT_SUM, ONE_YJK_AMT, ifnull(sum(ZS_YJK_CNT),0) as SUM_ZS_YJK_CNT, ZS_AMT_SUM, CNT_TOL, PER_CNT, AMT_TOL, PER_AMT, ifnull(sum(DZKH_ZS_NUM),0) as SUM_DZKH_ZS_NUM, ifnull(sum(PLJH_ZS_NUM),0) as SUM_PLJH_ZS_NUM, ifnull(sum(JZZS_CZ_NUM),0) as SUM_JZZS_CZ_NUM, opr_dt, opr_tm FROM sum_yjk_2001_czy_ss
where 1=1
and aud_trm >= #{params.hz_startMonth}
and aud_trm <= #{params.hz_endMonth}
and cmcc_prov_prvd_id = #{params.provId}
and cmcc_prov_id = #{params.cityId}
order by aud_trm desc ,yjk_cnt desc,opr_id

SELECT gen_date, aud_trm, cmcc_prov_prvd_id, short_name, cmcc_prov_id, cmcc_prvd_nm_short, opr_id, concat(left(nm,1),'**') nm, yjk_cnt, amt_sum, one_yjk_amt, zs_yjk_cnt, zs_amt_sum, cnt_tol, per_cnt, amt_tol, per_amt, dzkh_zs_num, pljh_zs_num, jzzs_cz_num, opr_dt, opr_tm FROM sum_yjk_2001_czy_ss
where 1=1
and aud_trm >= #{params.hz_startMonth}
and aud_trm <= #{params.hz_endMonth}
and cmcc_prov_prvd_id = #{params.provId}
and cmcc_prov_id = #{params.cityId}
order by aud_trm desc ,yjk_cnt desc,pljh_zs_num desc

SELECT gen_date, aud_trm, cmcc_prov_prvd_id, short_name, cmcc_prov_id, cmcc_prvd_nm_short, opr_id, concat(left(nm,1),'**') nm, yjk_cnt, amt_sum, one_yjk_amt, zs_yjk_cnt, zs_amt_sum, cnt_tol, per_cnt, amt_tol, per_amt, dzkh_zs_num, pljh_zs_num, jzzs_cz_num, opr_dt, opr_tm FROM sum_yjk_2001_czy_ss
where 1=1
and aud_trm >= #{hz_startMonth}
and aud_trm <= #{hz_endMonth}
and cmcc_prov_prvd_id = #{provId}
and cmcc_prov_id = #{cityId}
order by aud_trm desc ,yjk_cnt desc,opr_id

select gen_date, aud_trm, cmcc_prov_prvd_id, short_name, cmcc_prov_id, cmcc_prvd_nm_short, opr_dt, opr_tm, opr_id, concat(left(nm,1),'**') nm, yjk_ser_no, yjk_typ, yjk_stat, yjk_amt, opr_typ, opr_nbr, yjk_pres_dt, user_id, (CASE WHEN MSISDN= '' THEN '' ELSE CONCAT(SUBSTR(MSISDN,1,(LENGTH(MSISDN)-4)),'****') END)AS msisdn, send_mon_arpu, send_lastm_arpu, send_last2m_arpu, send_avg_arpu, yjk_offer_cd, offer_nm, offer_cls, (CASE WHEN cz_msisdn= '' THEN '' ELSE CONCAT(SUBSTR(cz_msisdn,1,(LENGTH(cz_msisdn)-4)),'****') END)AS cz_msisdn from hpeapb.det_yjk_2001_ss c
and c.aud_trm >= #{params.mx_startMonth}
and c.aud_trm <= #{params.mx_endMonth}
and c.cmcc_prov_prvd_id = #{params.provId}
and c.cmcc_prov_id = #{params.mx_cityCode}
order by aud_trm desc,cmcc_prov_prvd_id,opr_dt desc

select aud_trm, cmcc_prov_prvd_id, short_name, cmcc_prov_id, cmcc_prvd_nm_short, opr_dt, opr_tm, opr_id, concat(left(nm,1),'**') nm, yjk_ser_no, yjk_typ, yjk_stat, yjk_amt, opr_typ, opr_nbr, yjk_pres_dt, user_id, (CASE WHEN MSISDN= '' THEN '' ELSE CONCAT(SUBSTR(MSISDN,1,(LENGTH(MSISDN)-4)),'****') END)AS msisdn, send_mon_arpu, send_lastm_arpu, send_last2m_arpu, send_avg_arpu, yjk_offer_cd, offer_nm, offer_cls, (CASE WHEN cz_msisdn= '' THEN '' ELSE CONCAT(SUBSTR(cz_msisdn,1,(LENGTH(cz_msisdn)-4)),'****') END)AS cz_msisdn from hpeapb.det_yjk_2001_ss c
and c.aud_trm >= #{mx_startMonth}
and c.aud_trm <= #{mx_endMonth}
and c.cmcc_prov_prvd_id = #{provId}
and c.cmcc_prov_id = #{mx_cityCode}
order by aud_trm desc,cmcc_prov_prvd_id,opr_dt desc LIMIT #{pageStar},#{pageEnd}
