mapper.select:
SELECT gen_date, aud_trm, cmcc_prov_prvd_id, short_name, exist_word, ifnull(sum(yjk_num),0) as sum_yjk_num, ifnull(sum(yjk_amt),0) as sum_yjk_amt FROM hpeapb.sum_yjk_2006_02_manu_prvd where 1=1
and aud_trm >= #{hz_startMonth}
and aud_trm <= #{hz_endMonth}
and cmcc_prov_prvd_id = #{provId}
group by exist_word order by exist_word

SELECT ifnull(sum(case when yjk_amt<10000 then yjk_amt else 0 end),0) as sum_yjk_amt_10000, ifnull(sum(case when yjk_amt>10000 and yjk_amt <=20000 then yjk_amt else 0 end),0) as sum_yjk_amt_20000, ifnull(sum(case when yjk_amt>20000 and yjk_amt <=50000 then yjk_amt else 0 end),0) as sum_yjk_amt_50000, ifnull(sum(case when yjk_amt>50000 and yjk_amt <=100000 then yjk_amt else 0 end),0) as sum_yjk_amt_100000, ifnull(sum(case when yjk_amt>100000 then yjk_amt else 0 end),0) as sum_yjk_amt_other, ifnull(count(yjk_amt <10000 or null),0) as count_yjk_amt_10000, ifnull(count(yjk_amt>10000 and yjk_amt<=20000 or null),0) as count_yjk_amt_20000, ifnull(count(yjk_amt>20000 and yjk_amt<=50000 or null ),0) as count_yjk_amt_50000, ifnull(count(yjk_amt>50000 and yjk_amt<=100000 or null),0) as count_yjk_amt_100000, ifnull(count(yjk_amt>100000 or null),0) as count_yjk_amt_other, gen_date, aud_trm, cmcc_prov_prvd_id, short_name, cmcc_prov_id, cmcc_prvd_nm_short, msisdn, user_type, org_nm, yjk_num FROM hpeapb.sum_yjk_2006_03_manu_msisdn
where 1=1
and aud_trm >= #{hz_startMonth}
and aud_trm <= #{hz_endMonth}
and cmcc_prov_prvd_id = #{provId}

SELECT gen_date, aud_trm, cmcc_prov_prvd_id, short_name, cmcc_prov_id, cmcc_prvd_nm_short, msisdn, user_type, org_nm, yjk_num, yjk_amt FROM hpeapb.sum_yjk_2006_03_manu_msisdn
where 1=1
and aud_trm >= #{params.hz_startMonth}
and aud_trm <= #{params.hz_endMonth}
and cmcc_prov_prvd_id = #{params.provId}
order by yjk_amt desc

